package javaPackage;

public class PostIncrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * int x=2; int y=x++;
		 * 
		 * System.out.println(y);
		 * 
		 * System.out.println(x);
		 */
		
		int i=1;
		System.out.println(i++);
		int j=i+1;
		System.out.println(i);
		System.out.println(j);

	}

}
