package javaPackage;

public class SquareCubeRoot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Square of 2 is :"+Math.pow(2, 2));
		
		System.out.println("Cube of 2 is :"+Math.pow(2, 3));
		
		System.out.println("Squareroot of 64 is:"+Math.sqrt(64));
		

	}

}
